export const DiscoverServersView: React.FC = () => {
  const [searchQuery, setSearchQuery] = useState('');
  const [searchResults, setSearchResults] = useState([]);
  const [selectedSource, setSelectedSource] = useState('npm');
  const [isLoading, setIsLoading] = useState(false);
  const [installProgress, setInstallProgress] = useState(null);

  // Search functionality
  const handleSearch = async () => {
    setIsLoading(true);
    try {
      const results = await invoke('search_mcp_servers', { 
        query: searchQuery,
        source: selectedSource
      });
      setSearchResults(results);
    } catch (error) {
      console.error('Search failed:', error);
    } finally {
      setIsLoading(false);
    }
  };

  // Installation handler
  const handleInstall = async (serverId) => {
    try {
      // Start installation and get progress updates
      await invoke('install_mcp_server', { 
        serverId,
        source: selectedSource,
        onProgress: (progress) => setInstallProgress(progress)
      });

      // Refresh installed servers list after completion
      await invoke('refresh_server_list');
    } catch (error) {
      console.error('Installation failed:', error);
    } finally {
      setInstallProgress(null);
    }
  };

  return (
    <div className="discover-servers-container">
      <h1>Discover MCP Servers</h1>

      <div className="source-selector">
        <button 
          className={selectedSource === 'npm' ? 'active' : ''} 
