        </button>
        <button 
          className={selectedSource === 'local' ? 'active' : ''} 
          onClick={() => setSelectedSource('local')}
        >
          Local Files
        </button>
      </div>

      <SearchBar 
        value={searchQuery} 
        onChange={setSearchQuery}
        onSearch={handleSearch}
        placeholder="Search for MCP servers..."
      />

      {isLoading ? (
        <div className="loading-indicator">Searching...</div>
      ) : (
        <div className="search-results">
          {searchResults.map(server => (
            <ServerCard
              key={server.id}
              server={server}
              onInstall={() => handleInstall(server.id)}
            />
          ))}
        </div>
      )}

      {installProgress && (
        <InstallationProgress progress={installProgress} />
      )}
    </div>
  );
};
